<?phpclass ConfigController extends Controller{    public function actionIndex()    {        $file = dirname(__FILE__).'../../../config/params.inc';        $content = file_get_contents($file);        $arr = unserialize(base64_decode($content));        $model = new ConfigForm();        $model->setAttributes($arr);		$templateid= $arr['template_about'];        if (Yii::app()->request->isPostRequest)        {			$template= Yii::app()->request->getPost('ConfigForm');			            $config = array(                        'template_about'=>$template['template_id'],                        );					$templateid = $template['template_id']	;            $str = base64_encode(serialize($config));            file_put_contents($file, $str);            Yii::app()->user->setFlash('config', Yii::t('app', 'Your new options have been saved.'));            $model->setAttributes($config);        }	        $this->render('index',array('model'=>$model , 'template_id'=>$templateid));    }}?>